										className="input"
										value={price}
										onChange={(e) => setPrice(e.target.value)}
										placeholder="Price"
									/>
								</div>
							</div>

							<div className="field">
								<div className="control">
									<button type="submit" className="button is-success">
										Update
									</button>
								</div>
							</div>
						</form>
					</div>
				</div>
			</div>
		</div>
	);
};

export default FormEditProduct;
